using System;
using System.Collections.Generic;
using System.IO;

namespace HiProtobuf.Lib
{
    public class ProtoGenerater
    {
        private string _path;
        public ProtoGenerater(string name)
        {
            name = name.Split('.')[0];
            _path = Common.ExportFolder + "/" + name + ".proto";
            if (File.Exists(_path))
            {
                File.Delete(_path);
            }
            var header = @"
// This is auto generated by HiProtobuf

syntax = ""proto3"";
package HiProtobuf;
option csharp_namespace = ""HiProtobuf""; 
";
            header += String.Format("message {0} {", name);

            File.WriteAllText(_path, header);
        }

        public void AddTypes(List<VariableInfo> infos)
        {
            string str = "";
            for (int i = 0; i < infos.Count; i++)
            {
                str += ProcessVariable(infos[i], i + 1);
            }
            File.WriteAllText(_path, "}");
        }

        /// <summary>
        /// 参照protobuf官网数据类型
        /// 数组用[]标识
        /// </summary>
        /// <param name="infos"></param>
        private string ProcessVariable(VariableInfo info, int index)
        {
            //double
            //float
            //int32
            //int64
            //uint32
            //uint64
            //sint32
            //sint64
            //fixed32
            //fixed64
            //sfixed32
            //sfixed64
            //bool
            //string
            //bytes
            string str = "";
            var type = info.Type;
            if (type.Contains("[]"))//如果是数组进行转换
            {
                type = "repeated " + type.Split('[')[0];
            }
            str += type + " " + info.Name + " = " + index + ";";
            str += "\n";
            return str;
        }
    }
}